    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // TODO add your handling code here:
        Calendar cal=Calendar.getInstance();
        int date=cal.get(Calendar.DATE);
        int month=cal.get(Calendar.MONTH)+1;
        int year=cal.get(Calendar.YEAR);
        String rev=date+"/"+month+"/"+year;
        String room="null";
        String price="null";
        if(jRadioButton1.isSelected())
       {
           room="AC";
           price="2500";
       }
        else if (jRadioButton2.isSelected())
       {
           room="DELUX";
           price="1500";
           
           
       }
        else
        {
            room="SIMPLE";
           price="1000"; 
        }
        int fd=Integer.valueOf((String)jComboBox2.getSelectedItem());
        int fm=Integer.valueOf((String)jComboBox3.getSelectedItem());
        int fy=Integer.valueOf((String)jComboBox4.getSelectedItem());
        cal.set(fy,fm,fd);
        long l1=cal.getTimeInMillis();
        int td=Integer.valueOf((String)jComboBox5.getSelectedItem());
        int tm=Integer.valueOf((String)jComboBox6.getSelectedItem());
        int ty=Integer.valueOf((String)jComboBox7.getSelectedItem());
        cal.set(ty,tm,td);
        long l2=cal.getTimeInMillis();
        int days=(int)(l2-l1)/(60000*60*24);
        int pric=Integer.parseInt(price);
        int total=days*pric;
        String name=jTextField1.getText();
        String mob=jTextField2.getText();
        
        String fdate=jComboBox2.getSelectedItem().toString();
        String fmonth=jComboBox3.getSelectedItem().toString();
        String fyear=jComboBox4.getSelectedItem().toString();
        String ffrom=fdate+"/"+fmonth+"/"+fyear;
       
        String tdate=jComboBox5.getSelectedItem().toString();
        String tmonth=jComboBox6.getSelectedItem().toString();
        String tyear=jComboBox7.getSelectedItem().toString();
        String tto=tdate+"/"+tmonth+"/"+tyear;
        
        
        
                
        try
        {
           
            Class.forName("com.mysql.jdbc.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql:///hotelreserv","root","");
            
           
           String query = "UPDATE reservation set reserv_date='"+rev+"', roomfrom='"+ffrom+"', roomto='"+tto+"', room='"+room+"', rentperday='"+price+"', days='"+days+"', total='"+total+"' where name='"+name+"' and mob='"+mob+"';";
           
           
           PreparedStatement pst=con.prepareStatement(query);
          
            int rs=pst.executeUpdate();
         
            
        if(rs>0 )
        {
            JOptionPane.showMessageDialog(this,"UPDATE");
            
        }
        
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(this,e);
        }
    }//GEN-LAST:event_jButton4ActionPerformed

