    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
        String name=jTextField1.getText();
        String mob=jTextField2.getText();
        try{
            Class.forName("com.mysql.jdbc.Driver");
            Connection con=DriverManager.getConnection("jdbc:mysql:///hotelreserv","root","");
            String query="select * FROM reservation where name='"+name+"' or mob='"+mob+"' ;";
            PreparedStatement pst=con.prepareStatement(query);
            ResultSet rs=pst.executeQuery();
            if(rs.next())
            {
                JOptionPane.showMessageDialog(this, "correct");

                try
                {
                    Class.forName("com.mysql.jdbc.Driver");
                    Connection con1=DriverManager.getConnection("jdbc:mysql:///hotelreserv","root","");
                    String query1="DELETE FROM reservation where name='"+name+"' or mob='"+mob+"' ;";
                    PreparedStatement pst1=con1.prepareStatement(query1);

                    pst1.executeUpdate();
                    con1.close();
                }
                catch(Exception e)
                {
                    e.printStackTrace();
                }
            }

            else
            {
                JOptionPane.showConfirmDialog(this,"invalid");
            }
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(this,e);
        }

    }//GEN-LAST:event_jButton6ActionPerformed

